import React, { useState, useEffect } from "react";
import axios from "axios";
import BlogCard from "../components/BlogCard";

const UserBlogs = () => {
  const [blogs, setBlogs] = useState([]);
  const [error, setError] = useState(null);

  // Get user blogs
  const getUserBlogs = async () => {
    try {
      const id = localStorage.getItem("userId");
      if (!id) {
        setError("User ID is missing.");
        return;
      }
      const { data } = await axios.get(`/api/v1/blog/user-blog/${id}`); // Fixed URL formatting
      if (data?.success) {
        setBlogs(data?.userBlog.blogs);
      } else {
        setError("Failed to fetch blogs.");
      }
    } catch (error) {
      console.error(error);
      setError("An error occurred while fetching blogs.");
    }
  };

  useEffect(() => {
    getUserBlogs();
  }, []); // Dependencies are not required unless `userId` changes

  return (
    <div>
      {error && <p>{error}</p>}
      {blogs.length > 0 ? (
        blogs.map((blog) => (
          <BlogCard
            key={blog._id} // Added unique key for each BlogCard
            id={blog._id}
            isUser={true}
            title={blog.title}
            description={blog.description}
            image={blog.image}
            username={blog.user.username}
            time={blog.createdAt}
          />
        ))
      ) : (
        <h1>You Haven't Created a Blog</h1>
      )}
    </div>
  );
};

export default UserBlogs;
