import React, { useState, useEffect } from 'react'
import axios from 'axios'

const Blogs = () => {
    const [blogs, setBlogs] = useState([])
    //get blogs

    const getAllBlogs = async () => {
        try {
            const { data } = await axios.get('/api/v1/blog/all-bog')  
            if (data?.success) {
                setBlogs(data.blogs)
            }
        } catch (error) {
            console.log(error);
        }
    }
    useEffect(() => {
        getAllBlogs();
    })
  return (
      <div>
          <h1>Blog Page</h1>
    </div>
  )
}

export default Blogs
